class CrossSessionReferenceEngine:
    """
    Kobler sammen sessions p√• tvers av platforms for optimization tracking
    """

    def __init__(self):
        self.session_database = {}
        self.optimization_patterns = {}
        self.success_metrics = {}

    def link_sessions(self, current_session_id: str, 
                     referenced_sessions: List[str],
                     link_type: str):
        """
        Lag explicit links mellom sessions for optimization tracking
        """
        if current_session_id not in self.session_database:
            self.session_database[current_session_id] = {
                'references': [],
                'referenced_by': [],
                'optimization_chain': []
            }

        for ref_session in referenced_sessions:
            self.session_database[current_session_id]['references'].append({
                'session_id': ref_session,
                'link_type': link_type,  # 'continuation', 'optimization', 'reference'
                'timestamp': datetime.now().isoformat()
            })

    def track_optimization_pattern(self, 
                                 pattern_name: str,
                                 sessions_involved: List[str],
                                 optimization_metrics: Dict[str, float]):
        """
        Track optimization patterns p√• tvers av sessions
        """
        self.optimization_patterns[pattern_name] = {
            'sessions': sessions_involved,
            'metrics': optimization_metrics,
            'improvement_trajectory': self.calculate_improvement_trajectory(optimization_metrics),
            'success_factors': self.identify_success_factors(sessions_involved)
        }
