        self.semantic_clusters = {}
        self.optimization_hooks = {}

    def index_conversation_content(self, session_id: str, content_chunks: List[Dict]):
        """
        Index faktisk innhold med semantic clustering for optimization
        """
        for chunk in content_chunks:
            # Semantic analysis av content
            semantic_vectors = self.extract_semantic_vectors(chunk['user_input'], 
                                                           chunk['assistant_response'])

            # Key concept extraction
            key_concepts = self.extract_key_concepts(chunk)

            # Optimization value assessment
            optimization_value = self.assess_optimization_value(chunk)

            indexed_content = {
                'session_id': session_id,
                'timestamp': chunk['timestamp'],
                'semantic_vectors': semantic_vectors,
                'key_concepts': key_concepts,
                'optimization_value': optimization_value,
                'raw_content': {
                    'user_input': chunk['user_input'],
                    'assistant_response': chunk['assistant_response']
                },
                'decision_points': chunk['decision_points'],
                'continuation_hooks': chunk['continuation_hooks']
            }

            self.content_index[f"{session_id}_{chunk['timestamp']}"] = indexed_content

    def find_optimization_patterns(self, query_concepts: List[str]) -> List[Dict]:
        """
        Find optimization patterns basert p√• faktisk content analyse
        """
        matching_content = []

        for content_id, content in self.content_index.items():
            concept_overlap = set(query_concepts) & set(content['key_concepts'])
            if concept_overlap and content['optimization_value'] > 0.7:
                matching_content.append({
                    'content_id': content_id,
                    'session_id': content['session_id'],
                    'relevance_score': len(concept_overlap) / len(query_concepts),
                    'optimization_value': content['optimization_value'],
                    'key_insights': self.extract_key_insights(content)
                })

        return sorted(matching_content, key=lambda x: x['optimization_value'], reverse=True)
